import React from 'react'
import PropTypes from 'prop-types'
import { NavLink } from 'react-router-dom'
import { Route, Switch } from 'react-router'
import { connect } from 'react-redux'

import Capsule from './Capsule'
import CapsuleEditor from './editor/CapsuleEditor'
import CapsuleHeader from './CapsuleHeader'
import CapsuleStats from './stats/CapsuleStats'
import CapsuleOutfits from './outfits/CapsuleOutfits'

import styles from './capsule.module.css'

export class CapsulePage extends React.Component {
  static propTypes = {}

  render () {
    return (
      <div>
        <CapsuleHeader
          capsule={this.props.capsule}
          location={this.props.location}
          match={this.props.match} />
        <Switch>
          <Route path={`${this.props.match.path}/outfits`} component={CapsuleOutfits} />
          <Route path={`${this.props.match.path}/edit`} component={CapsuleEditor} />
          <Route path={`${this.props.match.path}/stats`} component={CapsuleStats} />
          <Route exact path={`${this.props.match.path}`} component={Capsule} />
        </Switch>
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  capsule: state.firestore.data.capsules ?
    state.firestore.data.capsules[ownProps.match.params.id] :
    null
})

export default connect(mapStateToProps)(CapsulePage)
