import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { Button, Glyphicon } from 'react-bootstrap'
import { withRouter } from 'react-router'
import { Link } from 'react-router-dom'

import DeleteConfirmation from 'components/forms/DeleteConfirmation'

import styles from './outfitthumbnail.module.css'

export class OutfitThumbnail extends React.Component {
  static propTypes = {
    outfitID: PropTypes.string,
    garmentIDs: PropTypes.array,
    handleDelete: PropTypes.func
  }

  constructor () {
    super()

    this.state = {
      confirmDeleteActive: false
    }
  }

  redirect () {
    this.setState({ redirectToEdit: true })
  }

  render () {
    let garments

    // TODO:  Similar code duplicated elsewhere; try to extract.
    if (this.props.garmentIDs.length &&
      this.props.garments !== null &&
      this.props.images !== null ) {
      garments = this.props.garmentIDs.map(gID => {
        const garment = this.props.garments[gID]
        const image = this.props.images[garment.imageID]
        return <img key={gID} src={image.downloadURL} />
      })
    }

    const capsuleID = this.props.match.params.id
    const editURL = `/capsules/${capsuleID}/edit?outfit=${this.props.outfitID}`

    return (
      <div className='col-sm-4 col-md-3 col-lg-3'>
        <div className={styles.thumbnail}>
          { garments }
          <Link to={editURL}>
            <Glyphicon glyph="pencil" />
          </Link>
          <Button onClick={() => this.setState({confirmDeleteActive: true})}>
            <Glyphicon glyph="remove" />
          </Button>
        </div>

        <DeleteConfirmation
          active={this.state.confirmDeleteActive}
          message={'Are you sure you want to delete this outfit?'}
          onSubmit={() => this.props.handleDelete(this.props.outfitID)}
          onCancel={() => this.setState({confirmDeleteActive: false})} />

      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  uid: state.firebase.auth.uid,
  garments: state.firestore.data.garments ?
    state.firestore.data.garments :
    null,
  images: state.firestore.data.images ?
    state.firestore.data.images :
    null
})

export default compose(
  withRouter,
  connect(mapStateToProps, null),
)(OutfitThumbnail)
