import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { Button, Glyphicon } from 'react-bootstrap'

import Garment from 'components/Garment'

export class OutfitEditor extends React.Component {
  static propTypes = {
    garmentIDs: PropTypes.array,
    handleDelete: PropTypes.func
  }

  render () {
    let outfitGarments = []

    // TODO:  This is duplicated in Capsule.js & CapsuleEditor.js;
    // consolidate.
    if (this.props.garments !== null &&
      this.props.images !== null ) {
      outfitGarments = this.props.garmentIDs.map(gID => {
        const garment = this.props.garments[gID]
        const image = this.props.images[garment.imageID]
        return (
          <div key={gID} className='col-sm-4 col-md-4 col-lg-4'>
            <Garment
              garment={garment}
              image={image}>
              <Button onClick={() => this.props.handleDelete(gID)}>
                <Glyphicon glyph="remove" />
              </Button>
            </Garment>
          </div>
        )
      })
    }

    if (outfitGarments.length) {
      return <div>{ outfitGarments }</div>
    } else {
      return <div><p>Add clothes from the capsule to create your outfit.</p></div>
    }

  }
}

const mapStateToProps = (state, ownProps) => ({
  garments: state.firestore.data.garments ?
    state.firestore.data.garments :
    null,
  images: state.firestore.data.images ?
    state.firestore.data.images :
    null
})

export default compose(
  connect(mapStateToProps, null),
)(OutfitEditor)
