import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'

import Garment from 'components/Garment'

export class ShowFiveChart extends React.Component {
  static propTypes = {
    garmentsToShow: PropTypes.array // [{id: garment ID, count: outfit count}]
  }

  render () {
    let garmentsToShow
    if (this.props.garments &&
        this.props.images) {
      garmentsToShow = this.props.garmentsToShow.map(g => {
        const imageID = this.props.garments[g.id].imageID
        return (
          <Garment
            key={g.id}
            image={this.props.images[imageID]}>
            <p>{g.count} outfits</p>
          </Garment>
        )
      })
    }

    return (
      <div>
        {this.props.title}
        { garmentsToShow }
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  garments: state.firestore.data.garments ?
    state.firestore.data.garments :
    null,
  images: state.firestore.data.images ?
    state.firestore.data.images :
    null
})

export default compose(
  connect(mapStateToProps, null),
)(ShowFiveChart)
