import React from 'react'
import PropTypes from 'prop-types'
import { firestoreConnect } from 'react-redux-firebase'

import styles from './addgarmenttocapsuleform.module.css'

export class AddGarmentToCapsuleForm extends React.Component {
  static propTypes = {
    capsuleID: PropTypes.string,
    garmentID: PropTypes.string
  }

  constructor () {
    super()

    this.addGarmentToCapsule = this.addGarmentToCapsule.bind(this)
  }

  addGarmentToCapsule () {
    return this.props.firestore.set({ collection: 'capsules', doc: this.props.capsuleID },
      {garments: this.props.firestore.FieldValue.arrayUnion(this.props.garmentID)}, { merge: true })
      .then(function() {
        console.log("Document successfully written!");
      })
      .catch(function(error) {
        console.error("Error writing document: ", error);
      });
  }

  render() {
    return (
      <div className={styles.form}>
        <button onClick={this.addGarmentToCapsule}>Add to Capsule</button>
      </div>
    )
  }
}

export default firestoreConnect()(AddGarmentToCapsuleForm)
