import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import { Button, Glyphicon } from 'react-bootstrap'
import { Link } from 'react-router-dom'

import DeleteConfirmation from 'components/forms/DeleteConfirmation'
import Garment from 'components/Garment'
import { pluralize } from 'utilities/pluralize'
import { deleteGarmentFromCapsule, deleteOutfitsWithGarment } from 'shared/deleteMethods'

import styles from './capsule.module.css'

export class Capsule extends React.Component {
  static propTypes = {
    capsule: PropTypes.object,
    garments: PropTypes.object,
    images: PropTypes.object,
    outfits: PropTypes.array
  }

  constructor () {
    super()

    this.state = {
      confirmDeleteActive: false,
      garmentToDelete: null,
      outfitCount: null,
      outfitsLink: ''
    }
  }

  handleDelete (gID) {
    this.setState({ confirmDeleteActive: false })
    deleteGarmentFromCapsule(
      this.props.firestore,
      gID,
      this.props.match.params.id,
      this.props.capsule.garments)
      .then(() => {
        console.log('garment delete done; about to delete outfits')
        return deleteOutfitsWithGarment(this.props.firestore, gID, this.props.outfits)
      })
      .then(() => {
        console.log('outfit delete done')
      })
      .catch(error => {
        console.error("An error occurred: ", error);
      });
  }

  getOutfitCount (gID) {
    // TODO:  Someday would be good to display some kind of alert
    // for garments that are in the capsule but not in any outfits.
    let outfitCount = 0

    // TODO:  This calculates for every garment on every render; ok for now,
    // may need to cache somehow.  Might also be possible to store in firebase;
    // use a firebase function to update automatically?
    this.props.outfits.forEach(o => {
      if (o.garments.includes(gID)) {
        outfitCount++
      }
    })

    return outfitCount
  }

  render () {
    let capsuleGarments

    if (this.props.capsule !== null &&
      this.props.garments !== null &&
      this.props.images !== null &&
      this.props.outfits !== null ) {
      capsuleGarments = this.props.capsule.garments.map(gID => {
        const garment = this.props.garments[gID]
        const image = this.props.images[garment.imageID]

        const outfitCount = this.getOutfitCount(gID)
        const outfitsLink = `${this.props.location.pathname}/outfits?garment=${gID}`

        return (
          <div className='col-sm-4 col-md-3 col-lg-3' key={gID}>
            <Garment
              garment={garment}
              image={image}>
              <div className='garment-overlay'>
                <Link to={outfitsLink}>{outfitCount} {pluralize('Outfit', outfitCount)}</Link>
              </div>
              <Button onClick={() => this.setState({
                confirmDeleteActive: true,
                garmentToDelete: gID,
                outfitCount: outfitCount,
                outfitsLink: outfitsLink
              }) }>
                <Glyphicon glyph="remove" />
              </Button>
            </Garment>
          </div>
        )
      })
    }

    return (
      <div>
        { capsuleGarments }

        <DeleteConfirmation
          active={this.state.confirmDeleteActive}
          message={'Are you sure you want to delete this from the capsule?'}
          onSubmit={() => this.handleDelete(this.state.garmentToDelete)}
          onCancel={() => this.setState({
            confirmDeleteActive: false,
            garmentToDelete: null,
            outfitCount: null,
            outfitsLink: ''
          }) }>
            <p>
              <Link to={this.state.outfitsLink}>{this.state.outfitCount} outfits </Link>
              will also be deleted.</p>
            <p>
              If you want to keep <Link to={this.state.outfitsLink}>these outfits</Link>,
              edit them first to remove or replace the garment you want to delete.
            </p>
        </DeleteConfirmation>
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  uid: state.firebase.auth.uid,
  capsule: state.firestore.data.capsules ?
    state.firestore.data.capsules[ownProps.match.params.id] :
    null,
  garments: state.firestore.data.garments ?
    state.firestore.data.garments :
    null,
  images: state.firestore.data.images ?
    state.firestore.data.images :
    null,
  // Include only outfits belonging to this capsule
  outfits: state.firestore.ordered.outfits ?
    state.firestore.ordered.outfits.filter(o => (
      o.capsuleID === ownProps.match.params.id
    )) :
    null
})

export default compose(
  connect(mapStateToProps, null),
  firestoreConnect()
)(Capsule)
