import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'

import AddCapsuleForm from './forms/AddCapsuleForm'
import CapsuleThumbnail from './CapsuleThumbnail'

class CapsuleList extends Component {
  static propTypes = {
    uid: PropTypes.string,
    capsules: PropTypes.arrayOf(PropTypes.object)
  }

  render() {
    const capsuleItems = this.props.capsules.map(capsule => (
      <CapsuleThumbnail
        key={capsule.id}
        capsule={capsule} />
      )
    )
    return (
      <div>
        <h1>My Capsules</h1>
        <div>
          { capsuleItems }
        </div>
        <AddCapsuleForm />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  uid: state.firebase.auth.uid,
  capsules: state.firestore.ordered.capsules ?
    state.firestore.ordered.capsules :
    []
})

export default compose(
  connect(mapStateToProps, null),
)(CapsuleList)
