import React from 'react'
import PropTypes from 'prop-types'

import CapsuleAccordionGarment from './CapsuleAccordionGarment'
import { capitalize } from 'utilities/capitalize'

import styles from '../capsule.module.css'

import {
    Accordion,
    AccordionItem,
    AccordionItemTitle,
    AccordionItemBody,
} from 'react-accessible-accordion';

import 'react-accessible-accordion/dist/fancy-example.css';

const sortByCategory = garments => {
  let categories = {}
  for (const g of garments) {
    if (Object.keys(categories).includes(g.garment.category)) {
      // Add garment to existing category list
      categories[g.garment.category].push(g)
    } else {
      // Add new category along with garment
      categories[g.garment.category] = [g]
    }
  }
  return categories
}

export class CapsuleAccordion extends React.Component {
  static propTypes = {
    capsuleID: PropTypes.string,
    garments: PropTypes.array,
    handleAdd: PropTypes.func
  }

  render () {
    let accordionItems = []

    const categories = sortByCategory(this.props.garments)
    for (const c in categories) {
      const title = c
      const category = categories[c]
      let garments = category.map(item => (
        <CapsuleAccordionGarment
          key={item.garment.id}
          garment={item.garment}
          image={item.image}
          handleAdd={this.props.handleAdd} />
      ))

      accordionItems.push(
        <AccordionItem key={c}>
          <AccordionItemTitle>
            <h3>{ capitalize(title) }</h3>
          </AccordionItemTitle>
          <AccordionItemBody>
            <div className='row'>
              { garments }
            </div>
          </AccordionItemBody>
        </AccordionItem>
      )
    }

    return (
      <div className={styles.accordion}>
        <Accordion>
          { accordionItems }
        </Accordion>
      </div>
    )
  }
}

export default CapsuleAccordion
