import React from 'react'
import PropTypes from 'prop-types'
import { connect } from 'react-redux'
import { compose } from 'redux'

import Garment from 'components/Garment'

export class FavoriteGarmentChart extends React.Component {
  static propTypes = {
    favorite: PropTypes.object // {id: garment ID, count: outfit count}
  }

  render () {
    let favoriteGarment

    if (this.props.garments &&
        this.props.images) {
      const garmentID = this.props.favorite.id
      const imageID = this.props.garments[garmentID].imageID
      favoriteGarment = (
        <Garment
          image={this.props.images[imageID]}>
          <p>{this.props.favorite.count} outfits</p>
        </Garment>
      )
    }

    return (
      <div>
        FavoriteGarmentChart
        { favoriteGarment }
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  garments: state.firestore.data.garments ?
    state.firestore.data.garments :
    null,
  images: state.firestore.data.images ?
    state.firestore.data.images :
    null
})

export default compose(
  connect(mapStateToProps, null),
)(FavoriteGarmentChart)
